mapper.update:
UPDATE 
	tbl_admin
SET
    admin_pw = #{adminPw}
    , admin_name = #{adminName}
    , admin_email = #{adminEmail}
    , admin_position = #{adminPosition}
    , admin_login_date = NOW()
    , admin_branch = #{adminBranch}
WHERE 
	admin_id = #{adminId}

UPDATE tbl_admin
SET
	session_key = #{sessionId}
WHERE
	admin_id = #{adminId}
